
public class SwitchClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Print the alphabets in English Language

		String alphabet = "c";

		switch (alphabet) {
		case "a":
			System.out.println("alphabet is a");
			break;
		case "b":
			System.out.println("alphabet is b");
			break;
		case "c":
			System.out.println("alphabet is c");
			break;
		case "d":
			System.out.println("alphabet is d");
			break;
		case "e":
			System.out.println("alphabet is e");
			break;
		case "f":
			System.out.println("alphabet is f");
			break;
		default:
			System.out.println("alphabets can be from a to z only");
			break;
		}

	}

}
